import React, { Component } from 'react';
import axios from 'axios';
import './Employees.css';

export default class Employees extends Component {
  state = {
    data: [],
    isOpen: false,
    firstName: '',
    lastName: '',
    email: '',
  };

  componentDidMount() {
    axios.get('https://reqres.in/api/users?per_page=12').then((res) => {
      const data = res.data.data;
      this.setState({ data });
    });
  }

  deleteEmployee = (e) => {
    this.setState({
      data: this.state.data.filter((contact) => contact.id !== e),
    });
  };
  openForm = () => {
    this.setState({
      // data: [...this.state.data, { id: this.state.data.length + 1 }],
      isOpen: true,
    });
  };

  addEmployee = (e) => {
    this.setState({
      data: [...this.state.data, { id: this.state.data.length + 1, email: e }],
    });
  };

  onChangeFirstName = (e) => {
    this.setState({
      firstName: e.target.value,
    });
  };

  render() {
    console.log(this.state.data);
    const { data, isOpen, firstName, lastName, email } = this.state;
    return (
      <div>
        <button onClick={this.openForm}>
          Employee <i class='fa fa-plus' aria-hidden='true'></i>
        </button>

        <div className='list-employees'>
          {data.map((person) => (
            <div className='employee'>
              <img src={person.avatar} alt='avatar' />

              <div className='info'>
                {`${person.first_name} ${person.last_name}`}
                <div>{person.email}</div>
                <div onClick={() => this.deleteEmployee(person.id)}>
                  <i className='fa fa-trash' aria-hidden='true' />
                </div>
              </div>
            </div>
          ))}
        </div>
        {/* <button onClick={}>delete</button> */}
        {isOpen && (
          <div className='addEmployee'>
            <i class='fa fa-times' aria-hidden='true'></i>
            <input
              type='text'
              placeholder='First Name'
              value={firstName}
              onChange={this.onChangeFirstName}
            ></input>
            <input type='text' placeholder='Last Name' value={lastName}></input>
            <input type='email' placeholder='Email' value={email}></input>
            <button onClick={() => this.addEmployee(firstName)}>Add</button>
          </div>
        )}
      </div>
    );
  }
}
