import React, { Component } from 'react';
import axios from 'axios';
import './Employees.css';

export default class Employees extends Component {
  state = {
    data: [],
    isOpen: false,
  };

  componentDidMount() {
    axios.get('https://reqres.in/api/users?per_page=12').then((res) => {
      const data = res.data.data;
      this.setState({ data });
    });
  }

  deleteEmployee = (e) => {
    this.setState({
      data: this.state.data.filter((contact) => contact.id !== e),
    });
  };
  addEmployee = () => {
    this.setState({
      // data: [...this.state.data, { id: this.state.data.length + 1 }],
      isOpen: true,
    });
  };

  render() {
    console.log(this.state.data);
    const { data, isOpen } = this.state;
    return (
      <div>
        <div className='list-employees'>
          {data.map((person) => (
            <div className='employee'>
              <div className='name'>{`${person.first_name} ${person.last_name}`}</div>
              <div onClick={() => this.deleteEmployee(person.id)}>
                <i className='fa fa-trash' aria-hidden='true' />
              </div>
            </div>
          ))}
        </div>
        {/* <button onClick={}>delete</button> */}
        <button onClick={this.addEmployee}>add</button>
        {isOpen && <div className='addEmployee'>dfdfsfd</div>}
      </div>
    );
  }
}
