import React, { Component } from 'react';
import axios from 'axios';
import './Employees.css';

export default class Employees extends Component {
  state = {
    data: [],
    isOpen: false,
  };

  componentDidMount() {
    axios.get('https://reqres.in/api/users?per_page=12').then((res) => {
      const data = res.data.data;
      this.setState({ data });
    });
  }

  deleteEmployee = (e) => {
    this.setState({
      data: this.state.data.filter((contact) => contact.id !== e),
    });
  };
  addEmployee = () => {
    this.setState({
      // data: [...this.state.data, { id: this.state.data.length + 1 }],
      isOpen: true,
    });
  };

  render() {
    console.log(this.state.data);
    const { data, isOpen } = this.state;
    return (
      <div>
        <div className='list-employees'>
          {data.map((person) => (
            <div className='employee'>
              <img src={person.avatar} alt='avatar' />

              <div className='info'>
                {`${person.first_name} ${person.last_name}`}
                <div>{person.email}</div>
                <div onClick={() => this.deleteEmployee(person.id)}>
                  <i className='fa fa-trash' aria-hidden='true' />
                </div>
              </div>
            </div>
          ))}
        </div>
        {/* <button onClick={}>delete</button> */}
        <button onClick={this.addEmployee}>add</button>
        {isOpen && (
          <form className='addEmployee'>
            <ul>
              <li>
                <input
                  type='text'
                  name='field1'
                  // ref={(input) => {this.nameInput = input}}
                  defaultValue={''}
                  // className={`${styles.fieldStyle} ${styles.fieldSplit} ${styles.alignLeft}`}
                  placeholder='Name'
                />
                <input
                  type='tel'
                  name='field2'
                  // ref={(input) => {this.phoneInput = input}}
                  defaultValue={''}
                  // className={`${styles.fieldStyle} ${styles.fieldSplit} ${styles.alignRight}`}
                  placeholder='Phone'
                />
              </li>
              <li>
                <input
                  type='text'
                  name='field3'
                  // ref={(input) => {this.imgURLInput = input}}
                  defaultValue={''}
                  // className={`${styles.fieldStyle} ${styles.fieldFull}`}
                  placeholder='Image URL'
                />
              </li>
              <li>
                <input type='submit' value={'Add new contact'} />
              </li>
            </ul>
          </form>
        )}
      </div>
    );
  }
}
